mapper.select:
select
id, uuid, title, creator_id, date, place, start, end, type, members, desc, instance_id, status, create_time
from linework.tb_meeting


        and id = #{id}


        and uuid = #{uuid}


        and title = #{title}


        and creator_id = #{creatorId}


        and date = #{date}


        and place = #{place}


        and start = #{start}


        and end = #{end}


        and type = #{type}


        and members = #{members}


        and desc = #{desc}


        and instance_id = #{instanceId}


        and status = #{status}


        and create_time = #{createTime}
